import {useRef, useState} from 'react';
import { useEffect } from 'react';
import { motion, AnimatePresence, degrees } from "framer-motion";
import { div, p } from "framer-motion/client";
import Timer from './Timer';
import { useLanguage } from '../contexts/LanguageContext';


  const cardBase=[
   
`<svg 
 xmlns="http://www.w3.org/2000/svg"
 xmlns:xlink="http://www.w3.org/1999/xlink"
 viewBox="0 0 100 25" width="102%" height="auto" style="display:block">
<image  x="0px" y="0px" width="100" height="25"   xlink:href="data:img/png;base64," />
</svg>`,
`<svg 
 xmlns="http://www.w3.org/2000/svg"
 xmlns:xlink="http://www.w3.org/1999/xlink"
 viewBox="0 0 100 25" width="102%" height="auto" style="display:block">
<image  x="0px" y="0px" width="100" height="25"    xlink:href="data:img/png;base64," />
</svg>`,
`<svg 
 xmlns="http://www.w3.org/2000/svg"
 xmlns:xlink="http://www.w3.org/1999/xlink"
 viewBox="0 0 100 25" width="102%" height="auto" style="display:block">
<image  x="0px" y="0px" width="100" height="25"    xlink:href="data:img/png;base64," />
</svg>`
    ]



export default function QuestionCard(props ){
    const [isVisible,setIsVisible]=useState(true);
    const [count, setCount]=useState(3);
    const[showTimer,setShowTimer]=useState(false);
    const[flipped,setFlipped]=useState(false);
    const {t}=useLanguage();
  
    const RandomIndex=  Math.floor(Math.random()*cardBase.length);
    const RandomSvg=cardBase[RandomIndex];

    const [randomQuestion,setRandomQuestion]=useState(null);
    const [hasSelectedQuestion, setHasSelectedQuestion] = useState(false);
    
    const SelectAndRemoveQuestion=()=>{
      // Verificar que props.questions existe y tiene elementos
      if (!props.questions || !Array.isArray(props.questions) || props.questions.length === 0) {
        console.log('Questions no disponibles an:', props.questions);
        return;
      }
      
      // Solo seleccionar una vez
      if (hasSelectedQuestion) {
        return;
      }
      
      const index=Math.floor(Math.random()*props.questions.length);
      const questionSelected=props.questions[index];
      setRandomQuestion(questionSelected);
      setHasSelectedQuestion(true);
      console.log('Pregunta seleccionada:', questionSelected);
      
      const newQuestions=props.questions.filter((_,i)=>i!==index);
      props.setQuestions(newQuestions);
    
  };


  useEffect(()=>{
    // Solo ejecutar cuando las preguntas estn disponibles y no hayamos seleccionado an
    if (props.questions && props.questions.length > 0 && !hasSelectedQuestion) {
      SelectAndRemoveQuestion();
    }
  },[props.questions, hasSelectedQuestion]);

  useEffect(()=>{

    if (count>0) {
      const timer=setTimeout(()=>setCount(count-1),1000);
      return()=>clearTimeout(timer);
    }
    else{
      setShowTimer(true);
      setFlipped(true);
      console.log('FLIP ACTIVADO - flipped:', true, 'randomQuestion:', randomQuestion);
         
       const timeOut=setTimeout(()=>{
      props.onFlipComplete();
      
    },31000);
    return ()=> clearTimeout(timeOut);
    }
  },[count, props.onFlipComplete]);
    
    
  
    function exitComponent(){
        setIsVisible(false);
        setTimeout(()=>{
            onEnd();
        },150);    }


    return(
<>
    {showTimer&& <div className='absolute top-0 right-0'>
                  <Timer />
                   <button onClick={props.onFlipComplete} className='w-30 h-15 m-2 flex justify-center items-center rounded-4xl bg-pink-900 text-white text-3xl font-bold  transition-transform duration-300 ease-in-out transform hover:scale-110 hover:cursor-pointer'
         initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              transition={{ duration: 0.3, ease: "easeOut" }}>
           {t("game.skip")}
        </button>
                 </div>}

      <div className="perspective-1000" style={{ perspective: '1000px' }}>
        <motion.div 
          className="relative w-[90vw] h-[45vw] rounded-3xl m-2 sm:w-[80vw] sm:h-[40vw] lg:w-[60vw] lg:h-[30vw]"
          style={{ transformStyle: 'preserve-3d' }}
          initial={{ opacity: 0, rotateY: "0deg" }}
          animate={{ 
            opacity: 1,
            rotateY: flipped ? "180deg" : "0deg"
          }}
          transition={{ 
            opacity: { duration: 0.3 },
            rotateY: { duration: 1.2, ease: "easeInOut" }
          }}
        >
          {/* Cara frontal (contador) */}
          <motion.div
            className="absolute inset-0 flex justify-center items-center rounded-3xl bg-white shadow-lg"
            style={{ 
              backfaceVisibility: 'hidden',
              transform: 'rotateY(0deg)'
            }}
          > 
            <div className="text-8xl font-bold text-gray-700">
              {count > 0 ? count : null}
            </div>
          </motion.div>

          {/* Cara trasera (pregunta) */}
          <motion.div
            className="absolute inset-0 flex overflow-hidden flex-col items-center rounded-3xl bg-white shadow-lg"
            style={{ 
              backfaceVisibility: 'hidden',
              transform: 'rotateY(180deg)'
            }}
          >
            {randomQuestion && (
              <>
                <div className='h-[80%] flex items-center px-4'>
                  <p className='text-center font-bold text-gray-800 text-xl sm:text-2xl lg:text-3xl'>
                    {randomQuestion.question}
                  </p>
                </div>
                <div className='absolute -bottom-5 left-0 right-0 lg:-bottom-6' dangerouslySetInnerHTML={{__html:RandomSvg}}>
                </div>
              </>
            )}
          </motion.div>





             

              
         
          
        
        
          
  
        </motion.div>
      </div>
      </>
    );
}